let web3Instance = null

let currentAccount = null;

async function loadWallet() {
  if(!window.tronWeb) {
    return null
  }

  web3Instance = window.tronWeb
  currentAccount = window.tronWeb.defaultAddress.base58
  return window.tronWeb
}

function isConnected() {
  return !!window.tronWeb
}

function getAccount() {
  return !!window.tronWeb ? window.tronWeb.defaultAddress.base58 : null
}

function getWeb3Instance() {
  return window.tronWeb
}

export default {
  loadWallet,
  isConnected,
  getWeb3Instance,
  getAccount,
}
